document.addEventListener('DOMContentLoaded', function() {
            const shareButton = document.getElementById('shareButton');

            if (navigator.share) {
                // El navegador es compatible con la API Web Share
                shareButton.addEventListener('click', function() {
                    navigator.share({
                        title: document.title,
                        text: 'Echa un vistazo a este enlace',
                        url: window.location.href
                    })
                    .then(() => console.log('Enlace compartido con Ã©xito'))
                    .catch((error) => console.error('Error al compartir:', error));
                });
            } else {
                // El navegador no es compatible con la API Web Share
                shareButton.disabled = true;
                console.log('La API Web Share no es compatible en este navegador.');
            }
        });